import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import apiGiphyKey from "../config.js";
import GifGridItem from "./GifGridItem.jsx";

export const GifGrid = ({ categories }) => {
  const [images, setImage] = useState([]);

  useEffect(() => {
    getGif();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  //mounted solo se ejecuta una sola vez cuando el componente se monta

  //peticion HTTP
  const getGif = async () => {
    const url = `https://api.giphy.com/v1/gifs/search?q=${categories}&limit=10&api_key=${apiGiphyKey}`;
    const res = await fetch(url);
    const { data } = await res.json();
    const gifs = data.map((img) => {
      return {
        id: img.id,
        title: img.title,
        url: img.images?.downsized_medium?.url,
      };
    });
    console.log(gifs);
    setImage(gifs);
  };

  return (
    <div className="card-grid">
      <h1>{categories}</h1>
      <div>
        {images.map((img, index) => {
          return (
            <div key={index}>
              <GifGridItem {...img}/>
            </div>
          );
        })}
      </div>
    </div>
  );
};

GifGrid.propTypes = {
  categories: PropTypes.string,
};
