import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import GifGridItem from "./GifGridItem.jsx";
import { getGif } from "../helpers/getGif.js";

export const GifGrid = ({ categories }) => {
  const [images, setImage] = useState([]);

  useEffect(() => {
    getGifService(categories);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [categories]);
  //mounted solo se ejecuta una sola vez cuando el componente se monta
  //categories como dependencia significa que el codigo se estara ejecutando n cantidad de veces mientras cambie de estado la prop

  const getGifService = async (categories) => {
    console.log('no se ejecuta')
    try {
      let res = await getGif(categories);
      setImage(res);
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div>
      <h3>{categories}</h3>
      <div className="card-grid">
        {images.map((img, index) => {
          return <GifGridItem key={index} {...img} />;
        })}
      </div>
      {!images.length && <h1>No hay imagenes que mostrar</h1>}
    </div>
  );
};

GifGrid.propTypes = {
  categories: PropTypes.string,
};
