//use al inicio significa que es un hook los hooks no son mas que funciones

import { useState } from "react"

export const useFetchGifs = () => {

  const [state, setState] = useState({
      data:[],
      loading: true
  })

  setTimeout(()=>{
    setState({
        data:[1,2,3,4,5],
        loading: false
    })
  },2000)

  return state //{data:[], loading: true}
}
