import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import apiGiphyKey from "../config.js";

export const GifGrid = ({ categories }) => {
  const [count, setCount] = useState(0);

  useEffect(() => {
    getGif();
  },[]);

  //peticion HTTP
  const getGif = async () => {
    const url = `https://api.giphy.com/v1/gifs/search?q=${categories}&limit=10&api_key=${apiGiphyKey}`;
    const res = await fetch(url);
    const { data } = await res.json();
    const gifs = data.map((img) => {
      return {
        id: img.id,
        title: img.title,
        url: img.images?.downsized_medium?.url,
      };
    });
    console.log(gifs);
  };

  return (
    <div>
      <h1>{categories}</h1>
      <h3>{count}</h3>
      <button onClick={() => setCount(count + 1)}>Contar</button>
    </div>
  );
};

GifGrid.propTypes = {
  categories: PropTypes.string,
};
