import React from "react";
import PropTypes from "prop-types";
import apiGiphyKey from "../config.js";

export const GifGrid = ({ categories }) => {
  //peticion HTTP
  const getGif = async () => {
    const url = `https://api.giphy.com/v1/gifs/search?q=${categories}&limit=10&api_key=${apiGiphyKey}`;
    const res = await fetch(url);
    const {data} = await res.json();
    const gifs = data.map(img => {
        return {
            id: img.id,
            title: img.title,
            url: img.downsized_medium.url
        }
    })
    console.log(gifs);
  };
  getGif();
  return (
    <div>
      <h1>{categories}</h1>
    </div>
  );
};

GifGrid.propTypes = {
  categories: PropTypes.string,
};
